import React, { useState } from 'react'

function Signup() {
    const [formData, setformData] = useState({
        firstName:'',
        lastname:'',
        email:'',
        mobile:'',
        password:''
    })
    const onChangeHandler = (event) =>{
        console.log(event.target.value,event.target.name);
        const{name,value}=event.target;
        setformData((prev)=>{
            return {...prev,}
        })
    }
    return (
        <div className='form-div'>
            <h2>Registration Form</h2>
            <form>
                <label htmlFor=''>First Name</label>
                <input type='text' placeholder='First Name' name='firstName' onChange={onChangeHandler}/>
                <label htmlFor=''>Last Name</label>
                <input type='text' placeholder='Last Name' name='lastname' onChange={onChangeHandler}/>
                <label htmlFor=''>Email</label>
                <input type='email' placeholder='Email Address' name='email' onChange={onChangeHandler}/>
                <label htmlFor=''>Mobile Number</label>
                <input type='text' placeholder='Mobile Number' name='mobile' onChange={onChangeHandler}/>
                <label htmlFor=''>Password</label>
                <input type='password' placeholder='Password' name='password' onChange={onChangeHandler}/>
                <button type='submit'>Submit</button>
            </form>
        </div>
    )
}

export default Signup
