import React,{useState} from 'react'
import './Reminder.css'

function Reminder() {

  const [reminder, setReminder] = useState([]);
  const [newReminder, setNewReminder] = useState('');

  const handleEventReminder=(event)=>{
    setNewReminder(event.target.value)
    console.log(event.target.value)

  }
  const handleEventDisplay=()=>{
    if (newReminder.trim()){
      setReminder([...reminder,newReminder])
      console.log(reminder)
    }
  }
  return (
    <div className='container'>
        <h1>
            Reminder App
        </h1>
        <div className='div-input'>
        <input type="text" placeholder='Enter a reminder' onChange={handleEventReminder}/>
        <button className='btn-add' onClick={handleEventDisplay}>Add</button>
        </div>
    </div>
  )
}

export default Reminder
